import React,{ Component } from 'react';
import PropTypes from 'prop-types';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import classNames from 'classnames';
import { withStyles } from '@material-ui/core/styles';
import MobileStepper from '@material-ui/core/MobileStepper';
import KeyboardArrowLeft from '@material-ui/icons/KeyboardArrowLeft';
import KeyboardArrowRight from '@material-ui/icons/KeyboardArrowRight';


const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  root: {
    maxWidth: 400,
    flexGrow: 1,
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 200,
  },

  menu: {
    width: 200,
  },
  button: {
    margin: theme.spacing.unit,
  },
  formControl: {
    margin: '0 6px',
    width: '95%',
    marginTop: 19,
  },
  formContainer: {
    width: '100%',
    display: 'flex',
    alignItems: 'center',
    flexDirection: 'row',
    justifyContent: 'flex-end',
  },
});

class SliderExampleControlled extends Component {
  constructor(){
    super();
    this.state = {
      stepIndex: 0,

      desc: '',
      title: '',
      name: '',
    }
  }

  state = {
    activeStep: 0,
  };

  handleNext = () => {
    this.setState(state => ({
      activeStep: state.activeStep + 1,
    }));
  };

  handleBack = () => {
    this.setState(state => ({
      activeStep: state.activeStep - 1,
    }));
  };




  _handleTitleChange(event,value){this.setState({title: value});}
  _handleNameChange(event,value){this.setState({name: value});}
  _handleDescChange(event,value){this.setState({desc: value});}

  getStepContent(stepIndex) {
    const { classes } = this.props;

    switch (stepIndex) {
      case 0:
      return (

        <div>
        <TextField
        id="standard-dense"
        label="Nombre Completo"
        className={classNames(classes.textField, classes.formControl)}
        margin="dense"
        />
        <TextField
        id="standard-dense"
        label="Correo Electronico"
        className={classNames(classes.textField, classes.formControl)}
        margin="dense"/>
        <div className={classes.formContainer}>
        <Button variant="contained" color="default" className={classes.button} onClick={this.handlePrev.bind(this)}>Back</Button>
        <Button variant="contained" color="primary" className={classes.button} onClick={this.handleNext.bind(this)}>Next</Button>
        </div>
        </div>
        );

      case 1:
      return (
        <div>
        <TextField
        id="standard-dense"
        label="Usuario"
        className={classNames(classes.textField, classes.formControl)}
        margin="dense"
        />
        <TextField
        id="standard-dense"
        label="Contraseña"
        className={classNames(classes.textField, classes.formControl)}
        margin="dense"
        />
        <TextField
        id="standard-dense"
        label="Confirmar Contraseña"
        className={classNames(classes.textField, classes.formControl)}
        margin="dense"
        />
        <div className={classes.formContainer}>
        <Button variant="contained" color="default" className={classes.button} onClick={this.handlePrev.bind(this)}>Back</Button>
        <Button variant="contained" color="primary" className={classes.button} onClick={this.handleNext.bind(this)}>Next</Button>
</div>
        </div>
        );

      case 2:
      return (
        <div>
        <TextField
        id="standard-dense"
        label="Modelo del Dosificador"
        className={classNames(classes.textField, classes.formControl)}
        margin="dense"
        />
        <TextField
        id="standard-dense"
        label="N/S del Dosificador"
        className={classNames(classes.textField, classes.formControl)}
        margin="dense"
        />
        <div className={classes.formContainer}>
        <Button variant="contained" color="default" className={classes.button} onClick={this.handlePrev.bind(this)}>Back</Button>
        <Button variant="contained" color="secondary" className={classes.button} onClick={this.handleNext.bind(this)}>Finish</Button>
        </div>

        </div>);

      default:
      return 'You\'re a long way from home sonny jim!';
    }
  }

  render() {
    const { classes, theme } = this.props;
    return (<div>

            <MobileStepper
        variant="progress"
        steps={6}
        position="static"
        activeStep={this.state.activeStep}
        className={classes.root}
        nextButton={
          <Button size="small" onClick={this.handleNext} disabled={this.state.activeStep === 5}>
            Next
            {theme.direction === 'rtl' ? <KeyboardArrowLeft /> : <KeyboardArrowRight />}
          </Button>
        }
        backButton={
          <Button size="small" onClick={this.handleBack} disabled={this.state.activeStep === 0}>
            {theme.direction === 'rtl' ? <KeyboardArrowRight /> : <KeyboardArrowLeft />}
            Back
          </Button>
        }
      />



      <Stepper activeStep={this.state.stepIndex}>
      <Step>
      <StepLabel>Nombre y correo</StepLabel>
      </Step>
      <Step>
      <StepLabel>Creacion de usuario</StepLabel>
      </Step>
      <Step>
      <StepLabel>Ingresar dosificador</StepLabel>
      </Step>
      </Stepper>

      {this.getStepContent(this.state.stepIndex)}

      </div>


      );
  }

}
SliderExampleControlled.propTypes = {
  classes: PropTypes.object.isRequired,
  theme: PropTypes.object.isRequired,
};
export default withStyles(styles, { withTheme: true })(SliderExampleControlled);
