import React,{ Component, Fragment } from 'react';
import PropTypes from 'prop-types';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';


import classNames from 'classnames';
import { withStyles } from '@material-ui/core/styles';
import MenuItem from '@material-ui/core/MenuItem';

const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 200,
  },
  dense: {
    marginTop: 19,
  },
  menu: {
    width: 200,
  },
});

class SliderExampleControlled extends Component {
  constructor(){
    super();
    this.state = {
      stepIndex: 0,
      name: '',
      desc: 'Initial Description',
      title: ''
    }
  }

  handleNext() {
    let stepIndex = this.state.stepIndex;
    this.setState({
      stepIndex: stepIndex + 1,
    });
  }

  handlePrev() {
    let stepIndex = this.state.stepIndex;
    if (stepIndex > 0) {
      this.setState({
        stepIndex: stepIndex - 1
      });
    }
  }

  _handleTitleChange(event,value){this.setState({title: value});}
  _handleNameChange(event,value){this.setState({name: value});}
  _handleDescChange(event,value){this.setState({desc: value});}

  getStepContent(stepIndex) {

    switch (stepIndex) {
      case 0:
      return (





        <TextField
        label="Name"
        value={this.state.name}
        onChange={this._handleTitleChange.bind(this)}
        margin="normal"

        />

        );
      break;
      case 1:
      return (
        <TextField
        hintText="Description"
        floatingLabelText="description"
        type="text"
        value={this.state.desc}
        onChange={this._handleDescChange.bind(this)}
        />);
      break;
      case 2:
      return (
        <TextField
        hintText="Name"
        floatingLabelText="name"
        type="text"
        value={this.state.name}
        onChange={this._handleNameChange.bind(this)}
        />);
      break;
      default:
      return 'You\'re a long way from home sonny jim!';
    }
  }

  render() {
    const { classes } = this.props;


   console.log('render');
   return (<div>
    <Stepper activeStep={this.state.stepIndex}>
    <Step>
    <StepLabel>Select campaign settings</StepLabel>
    </Step>
    <Step>
    <StepLabel>Create an ad group</StepLabel>
    </Step>
    <Step>
    <StepLabel>Create an ad</StepLabel>
    </Step>
    </Stepper>

    {this.getStepContent(this.state.stepIndex)}

    <p onClick={this.handlePrev.bind(this)}>Back</p>
    <p onClick={this.handleNext.bind(this)}>Next</p>

    </div>
    );
 }

}
export default withStyles(styles)(SliderExampleControlled);
